import styled from "styled-components";

export const Container = styled.div`
  display: flex;
  align-items: center;
  flex-flow: column;
  overflow: hidden;
  margin: 48px auto 0;
  box-shadow: 0 0 20px rgba(0, 0, 0, 0.05), 0 0px 40px rgba(0, 0, 0, 0.08);
  border-radius: 5px;
  width: 60%;
  // padding-left: 10%;
  // padding-right: 10%;
`;

export const HeaderData = styled.th`
  padding-right: 12%;
  padding-top: 20px;
  padding-bottom: 20px;
  text-align: left;
  color: #707070;
  border-bottom: 2px solid #707070;
`;

export const Table = styled.table`
  border-spacing: 0px;
`;

export const Row = styled.tr`
  font-size: 0.9em;
  text-align: left;
  color: #707070;
`;

export const RowData = styled.td`
  padding-right: 12%;
  padding-top: 10px;
  padding-bottom: 10px;
  border-bottom: 1px solid #ededed;
`;
