import React from "react";
import { makeStyles, createStyles, Theme } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import LineTableRow from "./tableRow";
import styled from "styled-components";

export interface Props {
  lineData: Line[];
}

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      flexGrow: 1
    },
    table: {
      minWidth: 650
    }
  })
);

const Wrapper = styled.div`
  display: flex;
  align-items: center;
  flex-flow: column;
  // margin: 0 auto;
  // border: 2px solid #000;
  // border-radius: 20px;
  // background: #eee;
`;

const HeaderCell = styled.th`
  padding: 10px;
  text-align: right;
`;

const TrainLineTable = styled.table``;

const TrainLinesTable: React.FC<Props> = props => {
  const classes = useStyles();

  return (
    <Wrapper>
      <Grid container spacing={3} data-testid="grid-container">
        <Grid item xs={2} data-testid="grid-item-xs-2"></Grid>
        <Grid item xs={8} data-testid="grid-item-xs-8">
          <TrainLineTable data-testid="table">
            {/* Table Header */}
            <thead data-testid="table-head">
              <HeaderCell data-testid="table-head-line-cell">Line</HeaderCell>
              <HeaderCell data-testid="table-head-status-cell">
                Status
              </HeaderCell>
              <HeaderCell data-testid="table-head-severity-cell">
                Status Severity
              </HeaderCell>
              <HeaderCell data-testid="table-head-reason-cell">
                Status Reason
              </HeaderCell>
            </thead>

            {/* Table Body */}
            <tbody data-testid="table-body">
              {props.lineData.map((line, index) => (
                <LineTableRow index={index} line={line} />
              ))}
            </tbody>
          </TrainLineTable>
        </Grid>
      </Grid>
    </Wrapper>
  );
};

export default TrainLinesTable;

interface Line {
  $type: String;
  id: String;
  name: String;
  modeName: String;
  disruptions: String[];
  created: String;
  modified: String;
  lineStatuses: LineStatus[];
  routeSections: String[];
  serviceTypes: ServiceType[];
  crowding: Crowding;
}

interface LineStatus {
  $type: String;
  id: number;
  statusSeverity: number;
  statusSeverityDescription: String;
  reason: String;
  created: String;
  validityPeriods: ValidityPeriod[];
  disruption: Disruption;
}

interface Disruption {
  $type: String;
  category: String;
  categoryDescription: String;
  description: String;
  affectedRoutes: String[];
  affectedStops: String[];
  closureText: String;
}

interface ValidityPeriod {
  $type: String;
  fromDate: String;
  toDate: String;
  isNow: Boolean;
}

interface ServiceType {
  $type: String;
  name: String;
  uri: String;
}

interface Crowding {
  $type: String;
}
