import React from "react";
import { makeStyles, createStyles, Theme } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import LineTableRow from "./tableRow";

export interface Props {
  lineData: Line[];
}

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      flexGrow: 1
    },
    table: {
      minWidth: 650
    }
  })
);

const TrainLinesTable: React.FC<Props> = props => {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <Grid container spacing={3} data-testid="grid-container">
        <Grid item xs={2} data-testid="grid-item-xs-2"></Grid>
        <Grid item xs={8} data-testid="grid-item-xs-8">
          <Grid container spacing={5}>
            <Grid item xs={12}>
              <table>
                <tr>
                  <th>Line</th>
                  <th>Status</th>
                  <th>Status Severity</th>
                  <th>Status Reason</th>
                </tr>
                <tr>
                  <td>Jill</td>
                  <td>Smith</td>
                  <td>50</td>
                </tr>
                <tr>
                  <td>Eve</td>
                  <td>Jackson</td>
                  <td>94</td>
                </tr>
              </table>
            </Grid>
            <Grid item xs={12}>
              <TableContainer data-testid="table-container">
                <Table
                  className={classes.table}
                  aria-label="table"
                  data-testid="table"
                >
                  {/* Table Header */}
                  <TableHead data-testid="table-head">
                    <TableRow>
                      <TableCell
                        align="right"
                        data-testid="table-head-line-cell"
                      >
                        Line
                      </TableCell>
                      <TableCell
                        align="right"
                        data-testid="table-head-status-cell"
                      >
                        Status
                      </TableCell>
                      <TableCell
                        align="right"
                        data-testid="table-head-severity-cell"
                      >
                        Status Severity
                      </TableCell>
                      <TableCell
                        align="right"
                        data-testid="table-head-reason-cell"
                      >
                        Status Reason
                      </TableCell>
                    </TableRow>
                  </TableHead>

                  {/* Table Cells */}
                  <TableBody data-testid="table-body">
                    {props.lineData.map((line, index) => (
                      <LineTableRow index={index} line={line} />
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </Grid>
          </Grid>
        </Grid>
      </Grid>
    </div>
  );
};

export default TrainLinesTable;

interface Line {
  $type: String;
  id: String;
  name: String;
  modeName: String;
  disruptions: String[];
  created: String;
  modified: String;
  lineStatuses: LineStatus[];
  routeSections: String[];
  serviceTypes: ServiceType[];
  crowding: Crowding;
}

interface LineStatus {
  $type: String;
  id: number;
  statusSeverity: number;
  statusSeverityDescription: String;
  reason: String;
  created: String;
  validityPeriods: ValidityPeriod[];
  disruption: Disruption;
}

interface Disruption {
  $type: String;
  category: String;
  categoryDescription: String;
  description: String;
  affectedRoutes: String[];
  affectedStops: String[];
  closureText: String;
}

interface ValidityPeriod {
  $type: String;
  fromDate: String;
  toDate: String;
  isNow: Boolean;
}

interface ServiceType {
  $type: String;
  name: String;
  uri: String;
}

interface Crowding {
  $type: String;
}
